import axios from "axios";
import { useEffect, useRef, useState } from "react";
import { DateRange } from "react-date-range";
import { useLocation } from "react-router-dom";
import Navbar from "../../components/navbar/Navbar";
import "./booking.css";
const Booking = () => {
    const [date, setDate] = useState([
        {
          startDate: new Date(),
          endDate: new Date(),
          key: "selection",
        },
      ]);
  const [data, setData] = useState();
  const [isReserve, setIsReserve] = useState(false);
  const location = useLocation();
  const fullname = useRef();
  const email = useRef();
  const phonenumber = useRef();
  const cardnumber = useRef();

  useEffect(() => {
    axios
      .post("http://localhost:5000/hotels/hotelinformation", { id: location.state.id })
      .then((result) => {
        setData(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  const handleSubmit = (e) =>{
       console.log(fullname.current.value)
  }
  return (
    <div className="container">
      <Navbar></Navbar>
      {data && (
        <div className="information">
          <div className="info1">
            <h3>{data.name}</h3>
            <p>{data.desc}</p>
          </div>
          <div className="info2">
            <p>
              <b>${data.cheapestPrice}</b> (1 nights)
            </p>
            <button onClick={() => setIsReserve(!isReserve)}>
              Reserve orBooks Now!
            </button>
          </div>
        </div>
      )}
      {isReserve && (
        <div className="transaction">
          <div className="tran1">
            <h2>Dates</h2>
            <DateRange 
            editableDateInputs={true}
            onChange={(item) => setDate([item.selection])}
            moveRangeOnFirstSelection={false}
            ranges={date}
            minDate={new Date()}
            ></DateRange>
          </div>
          <div className="tran2">
            <form onChange={handleSubmit}>
              <h2>Reserve Info</h2>
              <label>Your Full Name:</label>
              <input ref={fullname} type="text" placeholder="Full Name"></input>
              <label>Your Email:</label>
              <input ref={email} type="email" placeholder="Email"></input>
              <label>Your Phone Number:</label>
              <input ref={phonenumber} type="text" placeholder="Phone Number"></input>
              <label>Your Identity Card Number:</label>
              <input ref={cardnumber} type="text" placeholder="Card Number"></input>
            </form>
          </div>
        </div>
      )}
      <div>
        <h1>Select rooms</h1>
        <div>
          <h6>Budget Double Room</h6>
        </div>
        <div></div>
      </div>
    </div>
  );
};

export default Booking;
