import axios from "axios";
import { useEffect, useState } from "react";
import { DateRange } from "react-date-range";
import { useLocation } from "react-router-dom";

const Booking = () => {
  const [data, setData] = useState();
  const location = useLocation();
  const id = location.state.id;
  useEffect(() => {
    axios
      .post("http://localhost:5000/hotels/hotelinformation", { id: id })
      .then((result) => {
        setData(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
  });
  return (
    <div>
      {data && (
        <div>
          <div>
            <h1>{data.name}</h1>
            <p>{data.desc}</p>
          </div>
          <div>
            <h1>{data.cheapestPrice}(1 night)</h1>
            <button>Reserve orBooks Now!</button>
          </div>
        </div>
      )}
      <div>
        <div>
            <DateRange></DateRange>
        </div>
        <div>
            <form>
                <h2>Reserve Info</h2>
                <label>Your Full Name</label>
                <input type="text" placeholder="Full Name"></input>
                <label>Your Email</label>
                <input type="text" placeholder="Email"></input>
                <label>Your Phone Number</label>
                <input type="text" placeholder="Phone Number"></input>
                <label>Your Identity Card Number</label>
                <input type="text" placeholder="Card Number"></input>
            </form>
        </div>
      </div>
    </div>
  );
};

export default Booking;
