import "./featured.css";
import { useState, useEffect } from "react";
import axios from 'axios'
const Featured = () => {
  const [images, setImages] = useState([]);
  const [cities,setCities] = useState();

  useEffect(() => {
    const fetchImages = async () => {
      try {
        // Gửi yêu cầu để lấy danh sách tên file ảnh
        const response = await axios.get('http://localhost:5000/hotels/homepage');
        const imageFiles = response.data;
        console.log(imageFiles);
        setCities(imageFiles);
        // Tạo URL cho mỗi ảnh
        // const imageUrls = imageFiles.map(file => `http://localhost:5000/uploads/${file}`);
        // setImages(imageUrls);
        // setCities(response.data.finalResult)

      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    fetchImages();
  }, []);

  return (
    <div className="featured">
      {cities.map((city) => 
    (
      <div className="featuredItem">
      <img
        src={`http://localhost:5000/uploads/${city.image}`}
        alt=""
        className="featuredImg"
      />
      <div className="featuredTitles">
        <h1>{city.city}</h1>
        <h2>{city.count} properties</h2>
      </div>
    </div>
    )
  ) }

      <div className="featuredItem">
        <img
          src={images[0]}
          alt=""
          className="featuredImg"
        />
        <div className="featuredTitles">
          <h1>Reno</h1>
          <h2>533 properties</h2>
        </div>
      </div>
      <div className="featuredItem">
        <img
          src={images[2]}
          className="featuredImg"
          alt="HCM city"
        />
        <div className="featuredTitles">
          <h1>Dublin</h1>
          <h2>123 properties</h2>
        </div>
      </div>
      <div className="featuredItem">
        <img
          src={images[1]}
          alt=""
          className="featuredImg"
        />
        <div className="featuredTitles">
          <h1>Austin</h1>
          <h2>532 properties</h2>
        </div>
      </div>
    </div>
  );
};

export default Featured;
