import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const Booking = () => {
    const [data, setData] = useState()
    const location = useLocation()
    const id = location.state.id;
    useEffect(() => {
        axios.post('http://localhost:5000/hotels/hotelinformation', {id : id})
        .then(result => {
            setData(result.data)
        }).catch(err => {
            console.log(err);
        })
    })
    return (<div>
        <div>
           <div>
            <h1>{data.name}</h1>
            <p>{data.desc}</p>
           </div>
           <div>
             <h1>{data.cheapestPrice}(1 night)</h1>
           </div>
        </div>
    </div>)
};

export default Booking;
