import Navbar from "./Navbar";
import './Login.css'
import { useEffect, useRef, useState } from "react";
import axios from 'axios'

const Login = () => {
    const email = useRef();
    const password = useRef();
    const [login, setLogin] = useState();
    const handleSubmit = (e) => {
        e.preventDefault();
        setLogin({email : email.current.value,password:password.current.value});
    }
    useEffect(() => {
        if(login){
            console.log(login)
            axios.post('http://localhost:5000/user', {login}).then(result => {
                console.log(result)
            }).catch(err=> console.log(err))
        }
    }, [login])
    return(<div>
        <Navbar/>
        <form className="form" onSubmit={handleSubmit}>
        <h1>Login</h1><br/>
        <input ref={email} type="email" id="email"></input><br></br>
        <input ref={password} type="password" id="password"></input><br/>
        <button  type="submit" className="button">Login</button>
    </form>
    </div>)
}

export default Login;
