import axios from "axios";
import { useEffect, useState } from "react";
import { DateRange } from "react-date-range";
import { useLocation } from "react-router-dom";
import Navbar from "../../components/navbar/Navbar";
import './booking.css';
const Booking = () => {
  const [data, setData] = useState();
  const [isReserve, setIsReserve] = useState(false)
  const location = useLocation();
  const id = location.state.id;
  useEffect(() => {
    axios
      .post("http://localhost:5000/hotels/hotelinformation", { id: id })
      .then((result) => {
        setData(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
  });
  return (
    <div>
        <Navbar></Navbar>
      {data && (
        <div className="information">
          <div className="info1">
            <h3>{data.name}</h3>
            <p>{data.desc}</p>
          </div>
          <div className="info2">
            <h1>{data.cheapestPrice}(1 night)</h1>
            <button onClick={() => setIsReserve(!isReserve)}>Reserve orBooks Now!</button>
          </div> 
        </div>
      )}
      {isReserve && (
        <div>
            <div>
            <DateRange></DateRange>
        </div>
        <div>
            <form>
                <h2>Reserve Info</h2>
                <label>Your Full Name</label>
                <input type="text" placeholder="Full Name"></input>
                <label>Your Email</label>
                <input type="text" placeholder="Email"></input>
                <label>Your Phone Number</label>
                <input type="text" placeholder="Phone Number"></input>
                <label>Your Identity Card Number</label>
                <input type="text" placeholder="Card Number"></input>
            </form>
        </div>
        </div>
      )}
      <div>
        <h1>Select rooms</h1>
        <div>
            <h6>Budget Double Room</h6>
        </div>
        <div>

        </div>
      </div>
    </div>
  );
};

export default Booking;
